<template>
  <div role="right" :data-theme="currentTheme">
    <vue-perfect-scrollbar ref="infoBar" :style="{ height: windowHeight + 'px' }">
      <div v-if="variableControl.new === true"></div>
      <div role="wrap" aria-label="wrap of right sidebar" v-else>
        <!--Start: Info-->
        <div class="info d_flex flex_column align_items_center py_3 px_3">
          <div class="img mb_3" :style="{backgroundImage: 'url('+srcDefault+')'}"></div>
          <h3>Ngọc Hân</h3>
          <div role="time">Hoạt động trên Messenger</div>
        </div>
        <!--End: Info-->

        <!--Start: Option more-->
        <app-option :currentTheme="currentTheme" />
        <!--End: Option more-->

        <!--Start: Support other-->
        <div class="suport">

        </div>
        <!--End: Support other-->

        <!--Start: image shared-->
        <div class="shared">
          <app-shared :currentTheme="currentTheme"/>
        </div>
        <!--End: image shared-->
      </div>
    </vue-perfect-scrollbar>
  </div>
</template>

<script>
import AppOption from "./option";
import AppShared from "./shared";
export default {
  components: {
    AppOption,
    AppShared
  },
  props: {
    currentTheme: {
      type: String,
      default: "dark"
    }
  },
  data() {
    return {
      windowHeight: 0,
      srcDefault: "data:image/jpeg;base64,"
    }
  },
  computed: {
    variableControl(){
      return this.$store.getters.caseControl;
    }
  },
  mounted() {
    this.$nextTick(function() {
      window.addEventListener('resize', this.getWindowHeight);
      this.getWindowHeight()
    })
  },
  beforeDestroy() {
    window.removeEventListener('resize', this.getWindowHeight);
  },
  methods: {
    getWindowHeight(event) {
      this.windowHeight = document.documentElement.clientHeight - 60;
    }
  }
}
</script>

